                                  return Container(
                                    color: Colors.blue,
                                    margin: const EdgeInsets.all(5),
                                    child: Column(
                                      crossAxisAlignment: CrossAxisAlignment.start,
                                      children: [
                                        SelectableText(
                                          'Token Name: ${userPortfolio.data.tokens[index].tokenName}',
                                          style: const TextStyle(color: Colors.white, fontSize: 20),
                                        ),
